import{_ as e,c as t,o as s,a as r}from"./app.233e65ae.js";const V='{"title":"\u4EC0\u4E48\u662F VitePress\uFF1F","description":"","frontmatter":{"sidebarDepth":2,"title":"\u4EC0\u4E48\u662F VitePress\uFF1F"},"headers":[{"level":2,"title":"\u52A8\u673A","slug":"motivation"},{"level":2,"title":"\u76F8\u8F83\u4E8E VuePress v1 \u7684\u6539\u8FDB","slug":"improvements-over-vuepress-v1"},{"level":3,"title":"\u4F7F\u7528 Vue 3","slug":"it-uses-vue-3"},{"level":3,"title":"\u5E95\u5C42\u4F7F\u7528 Vite","slug":"it-uses-vite-under-the-hood"},{"level":3,"title":"\u9875\u9762\u66F4\u8F7B\u91CF\u5316","slug":"lighter-page-weight"},{"level":2,"title":"\u5176\u4ED6\u5DEE\u5F02","slug":"lighter-page-weight-1"},{"level":2,"title":"\u8FD9\u4F1A\u6210\u4E3A\u672A\u6765\u7684\u4E0B\u4E00\u4E2A VuePress \u5417\uFF1F","slug":"will-this-become-the-next-vuepress-in-the-future"}],"relativePath":"index.md"}',i={},a=r('<h1 id="what-is-vitepress" tabindex="-1">\u4EC0\u4E48\u662F VitePress\uFF1F <a class="header-anchor" href="#what-is-vitepress" aria-hidden="true">#</a></h1><div class="warning custom-block"><p class="custom-block-title">\u8B66\u544A</p><p>VitePress \u76EE\u524D\u662F 0.x \u7684\u72B6\u6001\u3002\u5B83\u5DF2\u7ECF\u9002\u5408\u4E8E\u5F00\u7BB1\u5373\u7528\u7684\u6587\u6863\u4F7F\u7528\uFF0C\u4F46\u662F\u914D\u7F6E\u548C\u4E3B\u9898 API \u53EF\u80FD\u4ECD\u7136\u4F1A\u5728\u5C0F\u7248\u672C\u4E4B\u95F4\u53D1\u751F\u53D8\u5316\u3002</p></div><p>VitePress \u662F \u5EFA\u7ACB\u5728 <a href="https://github.com/vitejs/vite" target="_blank" rel="noopener noreferrer">Vite</a> \u4E4B\u4E0A\u7684 <a href="https://vuepress.vuejs.org/" target="_blank" rel="noopener noreferrer">VuePress</a> \u5C0F\u5144\u5F1F\u3002</p><h2 id="motivation" tabindex="-1">\u52A8\u673A <a class="header-anchor" href="#motivation" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u559C\u6B22 VuePress v1\uFF0C\u4F46\u662F\u7531\u4E8E\u6784\u5EFA\u5728 Webpack \u4E4B\u4E0A\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u53EA\u6709\u51E0\u4E2A\u9875\u9762\u7684\u7B80\u5355\u6587\u6863\u7AD9\u70B9\u6765\u8BF4\uFF0C\u542F\u52A8\u5F00\u53D1\u670D\u52A1\u5668\u6240\u82B1\u8D39\u7684\u65F6\u95F4\u7B80\u76F4\u4EE4\u4EBA\u96BE\u4EE5\u5FCD\u53D7\u3002\u5373\u4F7F\u662F HMR \u66F4\u65B0\u4E5F\u53EF\u80FD\u9700\u8981\u6570\u79D2\u624D\u80FD\u53CD\u6620\u5230\u6D4F\u89C8\u5668\u4E2D\uFF01</p><p>\u4ECE\u6839\u672C\u4E0A\u8BF4\uFF0C\u8FD9\u662F\u56E0\u4E3A VuePress v1 \u5728\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A Webpack \u5E94\u7528\u7A0B\u5E8F\u3002\u5373\u4F7F\u53EA\u6709\u4E24\u4E2A\u9875\u9762\uFF0C\u5B83\u4E5F\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u9700\u8981\u7F16\u8BD1\u7684 Webpack \u9879\u76EE\uFF08\u5305\u62EC\u6240\u6709\u4E3B\u9898\u6E90\u6587\u4EF6\uFF09\u3002\u5F53\u9879\u76EE\u6709\u5F88\u591A\u9875\u9762\u65F6\uFF0C\u60C5\u51B5\u5C31\u66F4\u7CDF\u4E86\uFF0C\u56E0\u4E3A\u6BCF\u4E2A\u9875\u9762\u5728\u670D\u52A1\u5668\u663E\u793A\u4EFB\u4F55\u5185\u5BB9\u4E4B\u524D\u90FD\u5FC5\u987B\u5148\u5B8C\u5168\u7F16\u8BD1\uFF01</p><p>\u987A\u4FBF\u8BF4\u4E00\u53E5\uFF0CVite \u5F88\u597D\u5730\u89E3\u51B3\u4E86\u8FD9\u4E9B\u95EE\u9898\uFF1A\u670D\u52A1\u5668\u51E0\u4E4E\u7ACB\u5373\u542F\u52A8\uFF0C\u6309\u9700\u7F16\u8BD1\u6240\u670D\u52A1\u7684\u9875\u9762\uFF0C\u4EE5\u53CA\u5FEB\u901F\u7684 HMR\u3002\u53E6\u5916\uFF0C\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\uFF0C\u6211\u6CE8\u610F\u5230 VuePress v1 \u4E2D\u8FD8\u6709\u4E00\u4E9B\u53E6\u5916\u7684\u8BBE\u8BA1\u95EE\u9898\uFF0C\u4F46\u7531\u4E8E\u9700\u8981\u5927\u91CF\u7684\u91CD\u6784\uFF0C\u4E00\u76F4\u6CA1\u6709\u65F6\u95F4\u6765\u4FEE\u590D\u3002</p><p>\u73B0\u5728\uFF0C\u6709\u4E86 Vite \u548C Vue 3\uFF0C\u662F\u65F6\u5019\u91CD\u65B0\u8003\u8651 &quot;Vue \u9A71\u52A8\u7684\u9759\u6001\u7AD9\u70B9\u751F\u6210\u5668&quot; \u5230\u5E95\u53EF\u4EE5\u662F\u4EC0\u4E48\u4E86\u3002</p><h2 id="improvements-over-vuepress-v1" tabindex="-1">\u76F8\u8F83\u4E8E VuePress v1 \u7684\u6539\u8FDB <a class="header-anchor" href="#improvements-over-vuepress-v1" aria-hidden="true">#</a></h2><p>\u4E0E VuePress v1 \u76F8\u6BD4\uFF0CVitePress \u6709\u51E0\u4E2A\u6539\u8FDB...</p><h3 id="it-uses-vue-3" tabindex="-1">\u4F7F\u7528 Vue 3 <a class="header-anchor" href="#it-uses-vue-3" aria-hidden="true">#</a></h3><p>\u5229\u7528 Vue 3 \u6539\u8FDB\u7684\u6A21\u677F\u9759\u6001\u5206\u6790\u6765\u5C3D\u53EF\u80FD\u4E25\u683C\u5730\u7EA6\u675F\u9759\u6001\u5185\u5BB9\u3002\u9759\u6001\u5185\u5BB9\u4EE5\u5B57\u7B26\u4E32\u6587\u672C\u7684\u5F62\u5F0F\u53D1\u9001\uFF0C\u800C\u4E0D\u662F\u4EE5 JavaScript \u6E32\u67D3\u51FD\u6570\u4EE3\u7801\u7684\u5F62\u5F0F\uFF0C\u56E0\u6B64 JS \u8D1F\u8F7D\u89E3\u6790\u8D77\u6765\u5F00\u9500\u66F4\u5C0F\uFF0C\u6574\u5408\u8D77\u6765\u4E5F\u53D8\u5F97\u66F4\u5FEB\u3002</p><p>\u6CE8\u610F\uFF1A\u7528\u6237\u5728 markdown \u5185\u5BB9\u4E2D\u81EA\u7531\u6DF7\u5408 Vue \u7EC4\u4EF6\u65F6\uFF0C\u5E94\u7528\u4ECD\u7136\u4F1A\u88AB\u4F18\u5316\u2014\u2014\u7F16\u8BD1\u5668\u4F1A\u81EA\u52A8\u4E3A\u4F60\u8FDB\u884C\u9759\u6001/\u52A8\u6001\u5206\u79BB\uFF0C\u8FD9\u4E00\u70B9\u4F60\u65E0\u9700\u8003\u8651\u3002</p><h3 id="it-uses-vite-under-the-hood" tabindex="-1">\u5E95\u5C42\u4F7F\u7528 Vite <a class="header-anchor" href="#it-uses-vite-under-the-hood" aria-hidden="true">#</a></h3><ul><li>\u66F4\u5FEB\u7684\u5F00\u53D1\u670D\u52A1\u5668\u542F\u52A8</li><li>\u66F4\u5FEB\u7684\u70ED\u66F4\u65B0</li><li>\u66F4\u5FEB\u7684\u6784\u5EFA\uFF08\u5185\u90E8\u4F7F\u7528 Rollup\uFF09</li></ul><h3 id="lighter-page-weight" tabindex="-1">\u9875\u9762\u66F4\u8F7B\u91CF\u5316 <a class="header-anchor" href="#lighter-page-weight" aria-hidden="true">#</a></h3><ul><li>Vue 3 tree-shaking + Rollup \u4EE3\u7801\u62C6\u5206</li><li>\u4E0D\u4E3A\u6BCF\u4E2A\u8BF7\u6C42\u7684\u6BCF\u4E2A\u9875\u9762\u63D0\u4F9B\u5143\u6570\u636E\uFF0C\u4EE5\u4FBF\u5C06\u9875\u9762\u8D1F\u62C5\u4ECE\u603B\u9875\u9762\u4E2D\u5206\u79BB\u51FA\u6765\uFF0C\u4E14\u53EA\u53D1\u9001\u5F53\u524D\u9875\u7684\u5143\u6570\u636E\u3002\u5BA2\u6237\u7AEF\u5BFC\u822A\u540C\u65F6\u83B7\u53D6\u65B0\u9875\u9762\u7684\u7EC4\u4EF6\u548C\u5143\u6570\u636E\u3002</li><li>\u4E0D\u4F7F\u7528 <code>vue-router</code>\uFF0C\u56E0\u4E3A VitePress \u7684\u9700\u6C42\u975E\u5E38\u7B80\u5355\u548C\u5177\u4F53\uFF0C\u4F7F\u7528\u4E00\u4E2A\u7B80\u5355\u7684\u81EA\u5B9A\u4E49\u8DEF\u7531\u5668\uFF08\u4F4E\u4E8E 200 LOC\uFF09\u4EE3\u66FF\u3002</li><li>\uFF08WIP\uFF09i18n \u8BED\u8A00\u73AF\u5883\u6570\u636E\u4E5F\u5E94\u6309\u9700\u83B7\u53D6\u3002</li></ul><h2 id="lighter-page-weight-1" tabindex="-1">\u5176\u4ED6\u5DEE\u5F02 <a class="header-anchor" href="#lighter-page-weight-1" aria-hidden="true">#</a></h2><p>VitePress \u66F4\u56FA\u6267\u5DF1\u89C1\uFF0C\u53EF\u914D\u7F6E\u6027\u66F4\u4F4E\uFF1AVitePress \u7684\u76EE\u6807\u662F\u964D\u4F4E\u5F53\u524D VuePress \u7684\u590D\u6742\u6027\uFF0C\u5E76\u4ECE\u5176\u8D2F\u5F7B\u6781\u7B80\u4E3B\u4E49\u3002</p><p>VitePress \u9762\u5411\u672A\u6765\uFF1AVitePress \u53EA\u9488\u5BF9\u652F\u6301\u672C\u5730 ES \u6A21\u5757\u5BFC\u5165\u7684\u6D4F\u89C8\u5668\u3002\u5B83\u9F13\u52B1\u5728\u6CA1\u6709\u8F6C\u6362\u7684\u60C5\u51B5\u4E0B\u4F7F\u7528\u539F\u751F JavaScript\uFF0C\u5E76\u9F13\u52B1\u4F7F\u7528 CSS \u53D8\u91CF\u8FDB\u884C\u4E3B\u9898\u5316\u3002</p><h2 id="will-this-become-the-next-vuepress-in-the-future" tabindex="-1">\u8FD9\u4F1A\u6210\u4E3A\u672A\u6765\u7684\u4E0B\u4E00\u4E2A VuePress \u5417\uFF1F <a class="header-anchor" href="#will-this-become-the-next-vuepress-in-the-future" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u5DF2\u7ECF\u6709\u4E86 <a href="https://github.com/vuepress/vuepress-next" target="_blank" rel="noopener noreferrer">vuepress-next</a>\uFF0C\u8FD9\u5C06\u662F VuePress \u7684\u4E0B\u4E00\u4E2A\u4E3B\u8981\u7248\u672C\u3002\u5B83\u5728 VuePress v1 \u7684\u57FA\u7840\u4E0A\u505A\u4E86\u5F88\u591A\u6539\u8FDB\uFF0C\u73B0\u5728\u4E5F\u652F\u6301 Vite\u3002</p><p>VitePress \u4E0E\u5F53\u524D\u7684 VuePress \u751F\u6001\u7CFB\u7EDF\uFF08\u4E3B\u8981\u662F\u4E3B\u9898\u548C\u63D2\u4EF6\uFF09\u4E0D\u517C\u5BB9\u3002\u603B\u4F53\u601D\u8DEF\u662F\uFF0CVitePress \u5C06\u62E5\u6709\u4E00\u4E2A\u5927\u5927\u7B80\u5316\u7684\u4E3B\u9898 API\uFF08\u504F\u5411\u4E8E JavaScript API\uFF0C\u800C\u4E0D\u662F\u6587\u4EF6\u5E03\u5C40\u7EA6\u5B9A\uFF09\uFF0C\u800C\u4E14\u5F88\u53EF\u80FD\u6CA1\u6709\u63D2\u4EF6\uFF08\u6240\u6709\u5B9A\u5236\u90FD\u662F\u5728\u4E3B\u9898\u4E2D\u5B8C\u6210\u7684\uFF09\u3002</p>',23),h=[a];function l(u,n,o,p,d,v){return s(),t("div",null,h)}var g=e(i,[["render",l]]);export{V as __pageData,g as default};
